/* @jsx mdx */
import { mdx } from '@mdx-js/react';
/* @jsx mdx */
import { CodeSurfer as Surfer } from "code-surfer";
import { CodeSurferColumns, Step } from "code-surfer";
import { Appear, Background } from "gatsby-theme-mdx-deck";
import * as L from "../src/layout";
import customTheme from "../src/theme";
import GreetingLoader from "./src/greeting-loader";
import DanDemoBlocking from '../assets/dan-demo-blocking.png';
import DanDemoConcurrent from '../assets/dan-demo-concurrent.png';
import DeferredSetState from '../assets/deferred-setstate.svg';
import InterruptRendering from '../assets/interrupt-rendering.svg';
import HooksAPI from '../assets/hooks-api.svg';
import HooksAPITiming from '../assets/hooks-api-timing.svg';
import ComponentLifecycle from '../assets/component-lifecycle.svg';
import ObservingLifecycleEvents from '../assets/observing-lifecycle-events.svg';
import InterruptedLifecycle from '../assets/interrupted-lifecycle.svg';
import SchedulerPriorities from '../assets/scheduler-priorities.svg';
import Tearing1 from '../assets/bvaughn-tearing-1.png';
import Tearing2 from '../assets/bvaughn-tearing-2.png';
export const theme = customTheme;
export const _frontmatter = {};
const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {
  theme,
_frontmatter
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">


    <L.Column>
      <h1>{`Covid Outline`}</h1>
    </L.Column>
    <hr></hr>
    <CodeSurferColumns sizes={[1, 2]} mdxType="CodeSurferColumns">

      <Step subtitle="Here is a simple React component" mdxType="Step">

        <GreetingLoader mdxType="GreetingLoader" />
        <pre><code parentName="pre" {...{
            "className": "language-jsx",
            "metastring": "file=./src/greeting.1.1.js",
            "file": "./src/greeting.1.1.js"
          }}>{`import React from "react";
import Row from "./row";

export default function Greeting(props) {
  return (
    <section>
      <Row label="Name">{props.name}</Row>
    </section>
  );
}
`}</code></pre>
      </Step>
      <Step subtitle="we want to make the 'name' editable" mdxType="Step">

        <GreetingLoader mdxType="GreetingLoader" />
        <pre><code parentName="pre" {...{
            "7": true,
            "className": "language-jsx",
            "metastring": "7 file=./src/greeting.1.1.js",
            "file": "./src/greeting.1.1.js"
          }}>{`import React from "react";
import Row from "./row";

export default function Greeting(props) {
  return (
    <section>
      <Row label="Name">{props.name}</Row>
    </section>
  );
}
`}</code></pre>
      </Step>

      <Step subtitle="so we turn the component into a class" mdxType="Step">

        <GreetingLoader version="class.1.1" mdxType="GreetingLoader" />
        <pre><code parentName="pre" {...{
            "className": "language-jsx",
            "metastring": "file=./src/greeting.class.1.1.js",
            "file": "./src/greeting.class.1.1.js"
          }}>{`import React from "react";
import Row from "./row";

export default class Greeting extends React.Component {
  render() {
    return (
      <section>
        <Row label="Name">{this.props.name}</Row>
      </section>
    );
  }
}
`}</code></pre>
      </Step>
      <Step subtitle="add some state" mdxType="Step">
        <GreetingLoader version="class.1.2" mdxType="GreetingLoader" />
        <pre><code parentName="pre" {...{
            "className": "language-jsx",
            "metastring": "file=./src/greeting.class.1.2.js",
            "file": "./src/greeting.class.1.2.js"
          }}>{`import React from "react";
import Row from "./row";

export default class Greeting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "Mary"
    };
  }

  render() {
    return (
      <section>
        <Row label="Name">
          <input value={this.state.name} />
        </Row>
      </section>
    );
  }
}
`}</code></pre>
      </Step>
      <Step subtitle="and add a change handler that calls setState" mdxType="Step">
        <GreetingLoader version="class.1.3" mdxType="GreetingLoader" />
        <pre><code parentName="pre" {...{
            "className": "language-jsx",
            "metastring": "10:12,15:17,25 file=./src/greeting.class.1.3.js",
            "10:12,15:17,25": true,
            "file": "./src/greeting.class.1.3.js"
          }}>{`import React from "react";
import Row from "./row";

export default class Greeting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "Mary",
    };
    this.handleNameChange = this.handleNameChange.bind(
      this
    );
  }

  handleNameChange(e) {
    this.setState({ name: e.target.value });
  }

  render() {
    return (
      <section>
        <Row label="Name">
          <input
            value={this.state.name}
            onChange={this.handleNameChange}
          />
        </Row>
      </section>
    );
  }
}
`}</code></pre>
      </Step>
      <Step subtitle="we can now edit the name and it should work" mdxType="Step">
        <GreetingLoader version="class.1.3" mdxType="GreetingLoader" />
        <pre><code parentName="pre" {...{
            "className": "language-jsx",
            "metastring": "1:31 file=./src/greeting.class.1.3.js",
            "1:31": true,
            "file": "./src/greeting.class.1.3.js"
          }}>{`import React from "react";
import Row from "./row";

export default class Greeting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "Mary",
    };
    this.handleNameChange = this.handleNameChange.bind(
      this
    );
  }

  handleNameChange(e) {
    this.setState({ name: e.target.value });
  }

  render() {
    return (
      <section>
        <Row label="Name">
          <input
            value={this.state.name}
            onChange={this.handleNameChange}
          />
        </Row>
      </section>
    );
  }
}
`}</code></pre>
      </Step>
    </CodeSurferColumns>
    <hr></hr>
    <Background mdxType="Background" />
    <L.Column sx={{
      minHeight: '0'
    }}>
      <h2>{`Disclaimer`}</h2>
      <h3>{`Information in this talk may not be accurate or may change in the future`}</h3>
    </L.Column>
    <hr></hr>
    <L.Row>
      <h2>{`Time Slicing`}</h2>
      <h3>{`"Chunking slow rendering on subtree into little blocks of work by interrupting the work loop."`}</h3>
    </L.Row>
    <hr></hr>

    <img src={DanDemoBlocking} />
    <hr></hr>

    <img src={DanDemoConcurrent} />
    <hr></hr>
    <Background mdxType="Background" />
    <h3>{`"Wait a minute... I already know about this! What is this talk really about?"`}</h3>
    <hr></hr>
    <L.Column>
      <h1>{`Concurrent Mode`}</h1>
      <h2>{`A chain of unfortunate consequences`}</h2>
    </L.Column>
    <hr></hr>
    <Background mdxType="Background" />
    <h3>{`How does React "delay" rendering?`}</h3>
    <hr></hr>
    <L.Column>
      <h2>{`Sources of Slowdown`}</h2>
      <ul>
        <li parentName="ul">{`Slow component render functions`}</li>
        <li parentName="ul">{`Slow state setter or reducer updater functions`}</li>
        <li parentName="ul">{`Slow effect functions`}</li>
      </ul>
    </L.Column>
    <hr></hr>

    <L.Row>
      <h2>{`Deferred setState.`}</h2>
      <h3>{`Any state setter or dispatch can be delayed.`}</h3>
    </L.Row>
    <DeferredSetState mdxType="DeferredSetState" />
    <hr></hr>

    <L.Row>
      <h2>{`Interrupt Rendering.`}</h2>
      <h3>{`Any rendering can be interrupted and can be completed later before committing.`}</h3>
    </L.Row>
    <InterruptRendering mdxType="InterruptRendering" />
    <hr></hr>
    <L.Row>
      <h2>{`Oops #1`}</h2>
      <h3>{`Delaying rendering is only safe when no effects are pending on a subtree.`}</h3>
    </L.Row>
    <hr></hr>

    <L.Row>
      <h2>{`The Hooks API.`}</h2>
      <h3>{`This restriction is reflected in the hooks API with encapsulation of effects.`}</h3>
    </L.Row>
    <HooksAPI mdxType="HooksAPI" />
    <hr></hr>

    <L.Row>
      <h2>{`Hooks Timings.`}</h2>
      <h3>{`With each hook we observe a part of the lifecycle and can react.`}</h3>
    </L.Row>
    <HooksAPITiming mdxType="HooksAPITiming" />
    <hr></hr>

    <L.Row>
      <h2>{`The Lifecycle.`}</h2>
      <h3>{`The lifecycle we observe has three phases repeating.`}</h3>
    </L.Row>
    <ComponentLifecycle mdxType="ComponentLifecycle" />
    <hr></hr>

    <L.Row>
      <h2>{`Observing the Lifecycle.`}</h2>
      <h3>{`Overlapping the lifecycle with hooks, we see a phase that we can't observe...`}</h3>
    </L.Row>
    <ObservingLifecycleEvents mdxType="ObservingLifecycleEvents" />
    <hr></hr>

    <L.Row>
      <h2>{`Interrupting the Lifecycle.`}</h2>
      <h3>{`Unfortunately this phase becomes very relevant in Strict and Concurrent Mode.`}</h3>
    </L.Row>
    <InterruptedLifecycle mdxType="InterruptedLifecycle" />
    <hr></hr>
    <L.Row>
      <h2>{`Oops #2`}</h2>
      <h3>{`It's not possible to have a subscription or effect track the component lifecycle.`}</h3>
    </L.Row>
    <hr></hr>
    <L.Row>
      <h2>{`Workaround`}</h2>
      <h3>{`Resort to useSubscription`}</h3>
    </L.Row>
    <hr></hr>

    <L.Row>
      <h2>{`Scheduler Priorities.`}</h2>
      <h3>{`In Concurrent Mode the time budgets are switched on for time slicing.`}</h3>
    </L.Row>
    <SchedulerPriorities mdxType="SchedulerPriorities" />
